import { useState } from 'react';

function App() {
    const [values, setValues] = useState("");

    const handleClick = (e) => {
        setValues(values.concat(e.target.value));
    }
    const clearInput = () => {
        setValues("");
    }
    const backSpace = () => {
        setValues(values.slice(0, -1));
    }
    const evaluateResult = () => {
        //setValues(eval(values).toString());
    }
    return (
        <div className="App">
            <nav class="navbar navbar-dark bg-dark">
                <div class="container-fluid">
                    <a class="navbar-brand" href="#"></a>
                </div>
            </nav><nav class="navbar navbar-dark bg-dark">
                <div class="container-fluid">
                    <a class="navbar-brand" href="#">Calculator</a>
                </div>
            </nav><br />

            <h4>Calculator</h4><br />


            <div className='calculator'>
                <div className='calculator__output'>
                    <form>
                        <input
                            type='text'
                            value={values} readOnly />
                    </form>
                </div>
                <div className='form' >

                    <button onClick={clearInput} className="clear">C</button>
                    <button onClick={backSpace} className="backspace">BackSpace</button>
                    <button value="/"  onClick={handleClick}>/</button>
                    <button value="7" className='calculator__keys'onClick={handleClick}>7</button>
                    <button value="8" className='calculator__keys'onClick={handleClick}>8</button>
                    <button value="9" className='calculator__keys'onClick={handleClick}>9</button>
                    <button value="*" className="calculator__key calculator__key--enter"onClick={handleClick}>*</button>
                    <button value="4" className='calculator__keys'onClick={handleClick}>4</button>
                    <button value="5" className='calculator__keys'onClick={handleClick}>5</button>
                    <button value="6" className='calculator__keys'onClick={handleClick}>6</button>
                    <button value="-" onClick={handleClick}>-</button>
                    <button value="1" className='calculator__keys'onClick={handleClick}>1</button>
                    <button value="2" className='calculator__keys'onClick={handleClick}>2</button>
                    <button value="3" className='calculator__keys'onClick={handleClick}>3</button>
                    <button value="+" onClick={handleClick}>+</button>
                    <button value="0" className='calculator__keys'onClick={handleClick}>0</button>
                    <button value="." onClick={handleClick}>.</button>
                    <button onClick={evaluateResult} className='result' >=</button>

                </div>
            </div>
        </div>
    );
}

export default App;
