    }

    return (
        <div>
            <h4>Calculator</h4>
            <form>
                <input
                    type='text'
                    value={values}
                />
            </form>

            <div className='form'>

                <button type="button" onClick={clearInput} class="btn btn-secondary">C</button>{' '}
                <button type="button" onClick={backSpace} class="btn btn-secondary">B</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">/</button> <br />
                <button type="button" onClick={handleClick} class="btn btn-secondary">7</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">8</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">9</button>{' '}
