                <button type="button" onClick={handleClick} class="btn btn-secondary">4</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">5</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">6</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">-</button><br />
                <button type="button" onClick={handleClick} class="btn btn-secondary">1</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">2</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">3</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">+</button><br />
                <button type="button" onClick={handleClick} class="btn btn-secondary">0</button>{' '}
                <button type="button" onClick={handleClick} class="btn btn-secondary">.</button>{' '}
                <button type="button" onClick={evaluateResult} class="btn btn-secondary">Result</button>

            </div>

        </div>
    )
}

